import { Component, OnDestroy, OnInit, QueryList, TrackByFunction, ViewChildren } from '@angular/core';
import { NzImageDirective } from 'ng-zorro-antd/image';
import { interval, Subject } from 'rxjs';
import { delay, retryWhen, takeUntil } from 'rxjs/operators';
import { CctvAPI, LocalCamera } from 'src/app/api';

@Component({
  selector: 'app-camera-list',
  templateUrl: './camera-list.html',
  styleUrls: ['./camera-list.scss'],
})
export class CameraListComponent implements OnInit, OnDestroy {
  private destroy$ = new Subject<void>();

  fallback =
    'data:image/png;base64,';

  @ViewChildren(NzImageDirective)
  streams: QueryList<NzImageDirective>;

  cameras: LocalCamera[] = [];

  trackByCam: TrackByFunction<LocalCamera> = (_: number, cam: LocalCamera) => cam.id;

  constructor(
    private cctvapi: CctvAPI,
  ) { }

  ngOnInit() {
    this.cctvapi.listCameras()
      .pipe(
        takeUntil(this.destroy$),
        retryWhen(d => d.pipe(delay(1000)))
      )
      .subscribe(cams => this.cameras = cams);

    interval(5000)
      .pipe(
        takeUntil(this.destroy$),
      )
      .subscribe(() => {
        // reset streamBroken so we try again.
        this.streams?.forEach(dir => {
          if (dir.getElement().nativeElement.src === this.fallback) {
            const cam = this.cameras.find(c => c.streamUrl === dir.nzSrc);
            if (!!cam) {
              cam.streamUrl = this.cctvapi.getCameraStreamURL(cam.id) + `?nocache=${new Date().getTime()}`;
            }
          }
        })
      })
  }

  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
